","keccak256":"0x0ac286be11c2345f71d938119e638483fc1ddb9f56b7da00dd470adcd977f439","license":"MIT"},"contracts/Governance/CarbonTracker.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;
import "../NetEmissionsTokenNetwork.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

contract CarbonTracker is Initializable, ERC721Upgradeable, AccessControlUpgradeable {

    using SafeMathUpgradeable for uint256;
    using CountersUpgradeable for CountersUpgradeable.Counter;

    NetEmissionsTokenNetwork net;

    // Registered Tracker
    bytes32 public constant REGISTERED_TRACKER =
        keccak256("REGISTERED_TRACKER");
    /**
     * @dev tracker struct for incoming/ outgoing carbon tokens of the tracker
     * tokenIds - array of ids of carbon tokens (direct/indirect/offsets)
     * idIndex - mapping tokenId to its index in array. 1st index is 1, 0 reserved for unindexed
     * inAmount - mapping tokenId to incoming (burnt) amount
     * outAmount - mapping tokenId to outgoing amounts
    **/
    struct CarbonTokens{
        uint256[] tokenIds;
        mapping(uint => uint) idIndex; 
        mapping(uint => uint) inAmount;
        mapping(uint => uint) outAmount;
    }
    /**
     * @dev tracker details
     * trackee - address of the account the tracking will apply to
     * auditor -
     * totalEmissions - total amount of carbon in
     * totalAudited - total outgoing audited emission certificates
     * totalOffset - total outgoing offset credits
     *    
    **/
    struct CarbonTrackerDetails {
        uint trackerId;
        address trackee;    
        address auditor;    
        uint256 totalEmissions;
        uint256 totalAudited;
        uint256 totalOffset;
        uint256 fromDate;
        uint256 thruDate;
        uint256 dateCreated;
        string metadata;
        string description;
    }
    /** 
     * @dev tracker mappings
     * trackerIds - arrays of tracker ids
     * trackerIndex - mapping sourceTrackerId to index in array. 1st index is 1, 0 reserved for unindexed.
     * carbonTokens -  mapping sourceTrackerId (0 for none) to current CarbonTokens input/outputs. 
     * totalOut - mapping tokenId to total Carbon output amounts tracked
     * totalTracked - mapping tokenId for aggregate carbon (token type 4) tracked to other tracker ids
    */
    struct CarbonTrackerMappings {
        uint256[] trackerIds;
        mapping(uint256 => uint256) trackerIndex;
        mapping(uint256 => CarbonTokens) carbonTokens;
        mapping(uint256 => uint256) totalOut;
        mapping(uint256 => uint256) totalTracked;
    }

    mapping(uint => CarbonTrackerDetails) public _trackerData;
    mapping(uint => CarbonTrackerMappings) internal _trackerMappings;
    CountersUpgradeable.Counter private _numOfUniqueTrackers;

    // retired balances of tokenId tracked to address trackee
    mapping(uint => mapping(address => uint)) private _retiredBalances; 
    // transferredBalances balances of tokenId tracked to address trackee
    mapping(uint => mapping(address => uint)) private _transferredBalances; 
    // map audited emission token to a trackerId 
    mapping(uint => uint) public auditedTrackerId;
    // map offset credit token to a trackerId 
    mapping(uint => uint) public offsetTrackerId;

    // map verifier to trackee
    mapping(address => mapping (address => bool)) isVerifierApproved;

    event RegisteredTracker(address indexed account);
    event TrackerUpdated(
        uint256 indexed trackerId,
        address indexed tracker,
        uint256[] tokenIds,
        uint256[] inAmounts,
        uint256[] outAmounts,
        uint256[] trackerIds);
    event TrackeeChanged(uint indexed trackerId, address indexed trackee);
    event VerifierApproved(address indexed auditor,address indexed trackee);
    event VerifierRemoved(address indexed auditor,address indexed trackee);
    event VerifiedTransfer(uint indexed tokenId,address indexed trackee,uint amount );

    function initialize(address _net, address _admin) public initializer {
        net = NetEmissionsTokenNetwork(_net);
        __ERC721_init('NET Carbon Tracker', "NETT");
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
        _setupRole(REGISTERED_TRACKER, _admin);
    }  
    modifier notAudited(uint trackerId){
        require(_trackerData[trackerId].auditor==address(0),
            "CLM8::notAudited: trackerId is already audited"
        );
        _;
    }
    modifier isAuditor(uint trackerId){
        _isAuditor(trackerId);
        _;
    }
    function _isAuditor(uint trackerId) view internal{
        require(isVerifierApproved[msg.sender][_trackerData[trackerId].trackee],
            "CLM8::isAuditor: auditor is not approved by the trackee");
    }
    modifier trackerTokenkExists(uint256 tokenId){
        require(_numOfUniqueTrackers.current() >= tokenId,
            "CLM8::trackerTokenkExists: tracker token ID does not exist");
        _;
    }
    modifier registeredTracker(address trackee){
        require(hasRole(REGISTERED_TRACKER, trackee),
            "CLM8::registeredTracker: the address is not registered");
        _;
    }
    modifier onlyAdmin() {         
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "CLM8::onlyAdmin: msg.sender is not an admin");
        _;
    }
    /**
     * @dev require msg.sender has admin role
     */
    modifier selfOrAdmin(address _address){
        require( _address==msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "CLM8::selfOrAdmin: msg.sender does not own this address or is not an admin");               
        _;
    }

    function _verifyTotalTracked(uint256 outAmount, uint256 totalTracked) public pure {
        require(outAmount >= totalTracked,
            "CLM8::_verifyTotalTracked: total amount tracked exceeds output of tokenId from trackerId"
        );
    }

    function supportsInterface(bytes4 interfaceId) public view virtual 
        override(ERC721Upgradeable,AccessControlUpgradeable) returns (bool) {
        return interfaceId == type(IAccessControlUpgradeable).interfaceId || super.supportsInterface(interfaceId);
    }
    /**
     * @dev create a tracker Token for trackee. Note _track will check that msg.sender is either the trackee, or is an approved auditor of the trackee (see isVerifierApproved[][] mapping)
     * @param trackee - target adress to be tracked
     * @param tokenIds - array of ids of tracked carbon tokens (direct/indirect/offsets)
     * @param inAmounts - array of incoming token id amounts (direct/indirect/offsets) matching each carbon token
     * @param outAmounts - array of outgoing token id amounts (direct/indirect emissions) matching each carbon token
     * @param trackerIds - array of trackerIds matching each tokenID for tracing embodied emissions 
     */
    function track(
        address trackee,
        uint256[] memory tokenIds,
        uint256[] memory inAmounts,
        uint256[] memory outAmounts,
        uint256[] memory trackerIds,
        uint256 fromDate,
        uint256 thruDate,
        string memory metadata,
        string memory description
        ) public {
        // increment trackerId
        _numOfUniqueTrackers.increment();
        uint256 trackerId = _numOfUniqueTrackers.current();

        // create token details
        CarbonTrackerDetails storage trackerData = _trackerData[trackerId];
        trackerData.trackerId = trackerId;
        trackerData.trackee = trackee;
        trackerData.dateCreated = block.timestamp;

        _track(trackerData,tokenIds,inAmounts,outAmounts,trackerIds);
        _trackSetMetadata(trackerData,fromDate,thruDate,metadata,description);
        super._mint(msg.sender,trackerId);
    }
    /**
     * @dev update a tracker Token 
     * @param trackerId of the token
     * see tracker() function for description of other inputs
    **/ 
    function trackUpdate(
        uint trackerId,
        uint256[] memory tokenIds,
        uint256[] memory inAmounts,
        uint256[] memory outAmounts,
        uint256[] memory trackerIds,
        uint256 fromDate,
        uint256 thruDate,
        string memory metadata,
        string memory description) public 
        notAudited(trackerId) trackerTokenkExists(trackerId) {
        CarbonTrackerDetails storage trackerData = _trackerData[trackerId];
        _track(trackerData,tokenIds,inAmounts,outAmounts,trackerIds);
        _trackSetMetadata(trackerData,fromDate,thruDate,metadata,description);
    }
    function _trackSetMetadata(
        CarbonTrackerDetails storage trackerData,
        uint256 fromDate,
        uint256 thruDate,
        string memory metadata,
        string memory description) internal {
        if(fromDate>0){trackerData.fromDate = fromDate;}
        if(thruDate>0){trackerData.thruDate = thruDate;}
        if(bytes(metadata).length>0){trackerData.metadata = metadata;}
        if(bytes(description).length>0){trackerData.description = description;}
    }
    /**
     * @dev internal track operations used by track() and trackUpdate()
     * see trackerUpdate() for description of other inputs
    **/ 
    function _track(
        CarbonTrackerDetails storage trackerData,
        uint256[] memory tokenIds,
        uint256[] memory inAmounts,
        uint256[] memory outAmounts,
        uint256[] memory trackerIds) internal {
        require(inAmounts.length == tokenIds.length, 
            "CLM8::_track: inAmounts and tokenIds are not the same length"); 
        require(outAmounts.length == tokenIds.length, 
            "CLM8::_track: outAmounts and tokenIds are not the same length");
        require(trackerIds.length == tokenIds.length, 
            "CLM8::_track: trackerIds and tokenIds are not the same length");
        // create token details
        CarbonTrackerMappings storage trackerMappings = _trackerMappings[trackerData.trackerId];

        uint256 sourceTrackerId = trackerIds[0];
        CarbonTokens storage carbonTokens = trackerMappings.carbonTokens[sourceTrackerId];

        require(
            ( msg.sender == trackerData.trackee          
              //&& hasRole(REGISTERED_TRACKER, trackerData.trackee)
            ) ||
            (   net.isAuditor(msg.sender) &&
                isVerifierApproved[msg.sender][trackerData.trackee]),
            "CLM8::_track: msg.sender is not the registered trackee or an approved auditor"
        );
        uint8 tokenTypeId;
        for (uint i = 0; i < tokenIds.length; i++) { 
            // TO-DO write test to confirm this error.
            require(trackerData.trackerId != sourceTrackerId, 
                "CLM8::_track: trackerData.trackerId and sourceTrackerId can not be the same"); 
            tokenTypeId = net.getTokenTypeId(tokenIds[i]);
            if(i>0){
                 // get the data for the next tracker id if different
                if(trackerIds[i]!=trackerIds[i-1]){
                    sourceTrackerId = trackerIds[i];
                    carbonTokens = trackerMappings.carbonTokens[sourceTrackerId];
                }   
            }
            // verify retired emissions and set total emissions
            trackerData.totalEmissions = _verifyRetired(tokenIds[i], 
                trackerData.trackee, trackerData.totalEmissions,
                tokenTypeId, carbonTokens.inAmount[tokenIds[i]], inAmounts[i]);
            if(tokenTypeId==4){
                // asign trackerIds only for tokenTypeId==4             
                if(sourceTrackerId>0){ 
                    _updateTrackerSource(carbonTokens,
                        sourceTrackerId,tokenIds[i],inAmounts[i],outAmounts[i]);
                }
                trackerMappings.totalOut[tokenIds[i]] = _verifyTransferred(
                    trackerMappings.totalOut[tokenIds[i]], trackerData.trackee, 
                    tokenIds[i], carbonTokens.outAmount[tokenIds[i]],outAmounts[i]); 

                _verifyTotalTracked(trackerMappings.totalOut[tokenIds[i]],
                    trackerMappings.totalTracked[tokenIds[i]]); 
                // TO-DO how to prevent/resolve inconsistencies if attempting to
                // reduce tokenId totalOut after totalTracker has been increased?
            }else{
                // the following operations are for handing audited emissions and offset outputs
                if(sourceTrackerId>0){
                    // if user supplies a non-zero trackerId to this tokenId override
                    sourceTrackerId = 0;
                    // TO-DO: introduce a revert error (or event) to inform the user?
                    // sourceTrackerIds can only be assigned to tokenTypeId=4
                    // outgoing audited emission tokens are mapped using auditedTrackerIds 
                    // outoging offset tokens are mapped using offsetTrackerId
                }
                if(tokenTypeId==3){
                    if(isVerifierApproved[msg.sender][trackerData.trackee]){
                        //_isAuditor(trackerData.trackerId);
                        // only auditor can assign audited emissions as outgoing carbon token
                        // TO-DO use trackerId and associated tx hahses as inputs for the Fabric
                        // emissions auditing channel
                        // In addition to issuing a retired audited emisison token to issuee
                        // the audited emission are tied to a the trackerId (asigned to auditor) as outgoing emssions
                        // TO-DO If the issuee is an industry that has volunteered to be tracked by the auditor
                        // a new trackerId could be issued with the audited emissions as an input

                        if(carbonTokens.outAmount[tokenIds[i]]>0){
                            trackerData.totalAudited = trackerData.totalAudited
                                .sub(carbonTokens.outAmount[tokenIds[i]]);
                        }
                        if(outAmounts[i]>0){
                            trackerData.totalAudited = 
                                trackerData.totalAudited.add(outAmounts[i]);
                            auditedTrackerId[tokenIds[i]]=trackerData.trackerId;        
                        }else{
                            // if removing the outAmount delete the tracker mapping.
                            delete auditedTrackerId[tokenIds[i]];
                        }
                    }else{
                        // override outAmount to equal value already set by auditor
                        // TO-DO: introduce a revert error (or event) to inform the user?
                        outAmounts[i]=carbonTokens.outAmount[tokenIds[i]];
                    }
                }else if(tokenTypeId==2){
                    //To Do, set tracking data for offset credits
                }
            }   
            _updateTokenAmounts(carbonTokens,tokenIds[i],inAmounts[i],outAmounts[i]);

            if(i==tokenIds.length-1 || sourceTrackerId!=trackerIds[i+1]){
                // before moving to the next tracker Id update the trackerIds array
                _updateTrackerId(sourceTrackerId, trackerMappings, 
                    carbonTokens.tokenIds.length);
            }
        }
        require(trackerData.totalEmissions>=trackerData.totalAudited,
            "CLM8::_track: total audited emission out is greater than total emissions tracked");  
        emit TrackerUpdated(trackerData.trackerId,msg.sender,
            tokenIds,inAmounts,outAmounts,trackerIds);
    } 
    /**
     * @dev verify and set data for carbon tokenId (tokenTypeId=4) linked to another trackerId
     * TO-DO can the owner of sourceTrackerId authorize who is tracking to it?
     * Note the tracker must have received and burnt/retired (_verifyRetired) 
     * the same amount of carbon tokens. Is this enought to authorize who can reference sourceTrackerId 
     * @param carbonTokens of active trackerId
     * @param sourceTrackerId to update
     * @param tokenId being tracked
     * @param inAmount of tokenId
     * @param outAmount of tokenId
    **/ 
    function _updateTrackerSource(CarbonTokens storage carbonTokens, 
        uint256 sourceTrackerId, uint256 tokenId, 
        uint256 inAmount, uint256 outAmount) internal {
        // correct for previous amount assigned to the tokenId
        CarbonTrackerMappings storage sourceTracker = _trackerMappings[sourceTrackerId];
        if(carbonTokens.inAmount[tokenId]!=inAmount){
            if(carbonTokens.inAmount[tokenId]>0){
                sourceTracker.totalTracked[tokenId] = sourceTracker.totalTracked[tokenId]
                    .sub(carbonTokens.inAmount[tokenId]);
            }
            if(inAmount>0){
                sourceTracker.totalTracked[tokenId] = 
                    sourceTracker.totalTracked[tokenId].add(inAmount);
            }
        }
        if(carbonTokens.outAmount[tokenId]!=outAmount){
            if(carbonTokens.outAmount[tokenId]>0){
                sourceTracker.totalTracked[tokenId] = sourceTracker.totalTracked[tokenId]
                   .sub(carbonTokens.outAmount[tokenId]);
                }
            if(outAmount>0){
                sourceTracker.totalTracked[tokenId] = 
                    sourceTracker.totalTracked[tokenId].add(outAmount);
            }
        }
        _verifyTotalTracked(sourceTracker.totalOut[tokenId],
            sourceTracker.totalTracked[tokenId]);
    }
    /**
     * @dev update the token data within the Tacker
     * @param _tokenData to be updated 
     * @param tokenId to be added/removed/modified in _tokenData
     * @param inAmount - amount of input tokenId
     * @param outAmount - amount of output tokenId
    **/
    function _updateTokenAmounts(CarbonTokens storage _tokenData, 
        uint tokenId,uint inAmount,uint outAmount) internal {
        uint index = _tokenData.idIndex[tokenId];
        if(inAmount>0 || outAmount>0){
            // if the final amount is not zero check if the tokenId should be
            // added to the tokenIds array and update idAmount
            if(index==0){
                _tokenData.tokenIds.push(tokenId);
                _tokenData.idIndex[tokenId]=_tokenData.tokenIds.length;
            }
            _tokenData.inAmount[tokenId] = inAmount;
            _tokenData.outAmount[tokenId] = outAmount;  
        }else{
            // remove tokenId and associated data from tracker
            if (_tokenData.tokenIds.length > 1) {
                _tokenData.tokenIds[index-1] = 
                    _tokenData.tokenIds[_tokenData.tokenIds.length-1];
                _tokenData.idIndex[_tokenData.tokenIds[index-1]]=index;
            }
            // index of tokenId should be deleted;
            delete _tokenData.idIndex[tokenId];
            delete _tokenData.inAmount[tokenId];
            delete _tokenData.outAmount[tokenId];
            //delete _tokenData.idAudit[tokenId]
            delete _tokenData.tokenIds[_tokenData.tokenIds.length-1];
        }
    }
    /**
     * @dev update the token data within the Tacker
     * @param trackerMappings to be modified with trackerId
     * @param tokenIdsLength number of tokenIds mapped to a trackerId
    **/
    function _updateTrackerId(uint256 trackerId,
        CarbonTrackerMappings storage trackerMappings, uint256 tokenIdsLength) internal  {
        uint index = trackerMappings.trackerIndex[trackerId];
        if(tokenIdsLength>0){
            // if there are tracked tokenIds    
            if(trackerId>0 && index==0){
                // if the trackerId is not indexed (default is 0) 
                trackerMappings.trackerIds.push(trackerId);
                trackerMappings.trackerIndex[trackerId]
                    =trackerMappings.trackerIds.length;
            }
        }else{
            if(trackerId>0 && index>0){
                // if there are no tracked tokens (removed from tracker)
                // remove any non-zero trackerId from array, update indexing
                if (trackerMappings.trackerIds.length > 1) {
                    trackerMappings.trackerIds[index-1] = 
                        trackerMappings.trackerIds[trackerMappings.trackerIds.length-1];
                    trackerMappings.trackerIndex[trackerMappings.trackerIds[index-1]]=index;
                }
                delete trackerMappings.trackerIndex[trackerId];
                delete trackerMappings.trackerIds[trackerMappings.trackerIds.length-1];
            }
            // and finally delete carbonToken data if 
            delete trackerMappings.carbonTokens[trackerId];
        }
    }
    /**
     * @dev verify the amount of tokenId tracked as retired 
     * @param trackee for the tokenId 
     * @param tokenTypeId of the token being retired
     * @param amountOld previous amount retired
     * @param amountNew current amount to be retired
     * @param total amount of emission retired for this token Id. Also the @returns
    **/
    function _verifyRetired(uint tokenId, address trackee, uint total, 
        uint8 tokenTypeId, uint amountOld, uint amountNew) internal returns(uint) {
        if(amountOld!=amountNew){
            //adjust existing _retiredBalances
            _retiredBalances[tokenId][trackee]
                 =_retiredBalances[tokenId][trackee].sub(amountOld);
            if(tokenTypeId>2){
                total = total.sub(amountOld);
            }else if(tokenTypeId==2){
                total = total.add(amountOld);
            }// REC does not change the total emissions
        }
        if(amountNew>0){
            _retiredBalances[tokenId][trackee]=
                _retiredBalances[tokenId][trackee].add(amountNew);
            if(tokenTypeId>2){ //add in emission
                total = total.add(amountNew);
            }else if(tokenTypeId==2){ //subtract out offset
                total = total.sub(amountNew);
                // TO-DO: custom error message?
            }// else REC does not change the total emissions
            require(
                net.getRetiredBalances(tokenId,trackee) >= _retiredBalances[tokenId][trackee], 
                "CLM8::_verifyRetired: the retired balance exceeds what has been reported in NET"
            );
        }
        return total;
    }
    /**
     * @dev verify amount of tokenId listed as transferred (output)
     * @param totalOut tracked
     * @param tokenId being transfered
     * @param trackee transferring tokenId
     * @param amountOld previous balance listed transferred
     * @param amountNew current amount to listed as transferred
    **/
    function _verifyTransferred(uint totalOut, address trackee, uint tokenId, 
        uint amountOld, uint amountNew) internal returns (uint256) {
        if(amountOld!=amountNew && amountOld>0){
            //adjust existing _transferredBalances for tokenTypeId 4
            _transferredBalances[tokenId][trackee]
                =_transferredBalances[tokenId][trackee].sub(amountOld);
            totalOut = totalOut.sub(amountOld); 
        }
        if(amountNew>0){
            _transferredBalances[tokenId][trackee]
                =_transferredBalances[tokenId][trackee].add(amountNew);
            totalOut = totalOut.add(amountNew); 
            require(
                net.getTransferredBalances(tokenId,trackee) >= _transferredBalances[tokenId][trackee], 
                "CLM8::_verifyTransferred: the transferred balance exceeds what has been reported in NET"
            );
            emit VerifiedTransfer(tokenId,trackee,amountNew);
        }
        return totalOut;
    }

    function audit(uint trackerId) 
        public notAudited(trackerId) isAuditor(trackerId){
        _trackerData[trackerId].auditor=msg.sender;   
    }

    function removeAudit(uint trackerId) public isAuditor(trackerId){
        delete _trackerData[trackerId].auditor;
    }
    /**
     * @dev msg.sender can volunteer themselves as registered tracker or admin
     */
    function registerTracker(address tracker) selfOrAdmin(tracker) external
    {
        _setupRole(REGISTERED_TRACKER, tracker);
        emit RegisteredTracker(tracker);
    }
    /**
     * @dev change trackee of trackerId
     * @param trackerId - id of token tp be changed
     */
    function changeTrackee(uint trackerId, address trackee) external 
        onlyAdmin registeredTracker(trackee) trackerTokenkExists(trackerId){
        CarbonTrackerDetails storage trackerData = _trackerData[trackerId];
        trackerData.trackee = trackee;
        emit TrackeeChanged(trackerId,trackee);
    }    
    /**
     * @dev approve verifier for trackee as msg.sender
     * @param verifier to be approved or removed
     * @param approve (true) or remove (false)
     */
    function approveVerifier(address verifier,bool approve) 
        external {
        require(
            net.isAuditor(verifier) || !approve,
            "CLM8::approveVerifier: address is not a registered emissions auditor"
        );
        require(verifier!=msg.sender,
            "CLM8::approveVerifier: auditor cannot be msg.sender"
        );
        isVerifierApproved[verifier][msg.sender]=approve;
        if(approve){
            emit VerifierApproved(verifier,msg.sender);
        }else{
            emit VerifierRemoved(verifier,msg.sender);
        }
    }
    uint private constant MAX_NESTED_TRACKERS = 257; // 
    /**
     * These are public view functions
     * Warning: should never be called within functions that update the network to avoid excessive gas fees
    */
    // This struct stores the trackerIds used to calculate carbon intensity metircs
    // used to stop any circular referencing.
    // struct UsedTrackers {mapping(uint=>bool) trackerId;}
    function carbonIntensity(uint trackerId, uint8 tokenTypeId)
        public view returns (uint) {
        //UsedTrackers calldata trackerIds;
        uint[MAX_NESTED_TRACKERS] memory trackerIds;
        //UsedTrackers storage usedTrackerIds;
        return _carbonIntensity(trackerId,tokenTypeId,trackerIds);
    } 
    /**
     * @dev measure the carbon intensity of a tracker NFT. 
     *      This is a recursive function that cycle through all previous trackerIds
     * @param trackerId to measure
     * @param tokenTypeId measure total carbonIntensity of tracker token for tokenTypeId outputs (2. offset credits, audited emission certificates, carbon tracker tokens)
    */
    function _carbonIntensity(uint trackerId, uint8 tokenTypeId, uint[MAX_NESTED_TRACKERS] memory usedTrackerIds) 
        public view returns (uint) {
        //uint decimals = 1000000; // decimals used to calculate the final intensity metric (i.e., divide integer by this amount)

        if(trackerId==0){if(tokenTypeId==3){return 1000000;}else{return 0;}}
        // the carbon intensity of:
        //      - untracked audited emission certificate inputs = 1 (scaled by 1000000)
        //      - untracked burnt carbon offsets/tracker token inputs = 0
        CarbonTrackerDetails storage trackerData = _trackerData[trackerId];
        CarbonTrackerMappings storage trackerMappings = _trackerMappings[trackerId];

        uint total;//= trackerData.totalEmissions;
        uint outEmbedded; // embedded emissions associated with all outgoing CarbonTracker token amounts
        uint denominator; // of the carbon intensity metric
        uint offsets;
        //uint inAmount;
        //uint outAmount;
        uint ci;
        uint8 _tokenTypeId; 
        // start by calculating carbon intensity for tokenIds mapped to the 
        // default trackerId 0 (no trackerId assigned).
        trackerId = 0; 
        CarbonTokens storage carbonTokens = trackerMappings.carbonTokens[trackerId];
        for(uint i = 0; i <= trackerMappings.trackerIds.length; i++){
            // Will only calculate embedded emisisons upto MAX_NESTED_TRACKERS
            // Dont loop over tokens tracked back to previous tokenId (will cause recursive loop) 
            if(usedTrackerIds[usedTrackerIds.length-1]!=MAX_NESTED_TRACKERS 
                || contains(usedTrackerIds,trackerMappings.trackerIds[i])
                ){
                // trackerId and carbonTokens updated at the end of loop
                for (uint j = 0; j < carbonTokens.tokenIds.length; j++) {
                    /*if(trackerId>0){
                        //There is no need to verifyTotalTracked. This is done in 
                        //this is currenlty handled in _track() function afer
                        _verifyTotalTracked(
                            _trackerData[trackerId].carbonTokens.outAmount[tokenId],
                            _trackerData[trackerId].totalTracked[tokenId]);
                    } */
                    _tokenTypeId = net.getTokenTypeId(carbonTokens.tokenIds[j]);
                    //inAmount = carbonTokens.inAmount[carbonTokens.tokenIds[j]]
                    //    .mul(1000000);
                    //outAmount = carbonTokens.outAmount[carbonTokens.tokenIds[j]]
                    //    .mul(1000000);
                    if(_tokenTypeId==4){
                        ci = _carbonIntensity(trackerId,_tokenTypeId,usedTrackerIds);
                        total = total.add(carbonTokens.inAmount[carbonTokens.tokenIds[j]]
                            .mul(1000000));
                        outEmbedded=outEmbedded.add(carbonTokens.outAmount[carbonTokens.tokenIds[j]]
                            .mul(1000000).mul(ci).div(1000000));
                        denominator = denominator.add(
                            carbonTokens.outAmount[carbonTokens.tokenIds[j]]);
                    }else if(_tokenTypeId==3){
                        if(carbonTokens.outAmount[carbonTokens.tokenIds[j]]==0){
                            ci = _carbonIntensity(
                                auditedTrackerId[carbonTokens.tokenIds[j]],
                                _tokenTypeId,
                                usedTrackerIds);
                        }else{
                            // audited emissions out will always point back to the the tracker id
                            // set ci to 1, otherwise will enter recursive loop.
                            ci = 1000000;
                        }
                    }else if(_tokenTypeId==2){
                        // TO-DO setup how we handle offset tokens
                        ci = _carbonIntensity(
                            offsetTrackerId[carbonTokens.tokenIds[j]],
                            _tokenTypeId,
                            usedTrackerIds);
                        total = total.sub(carbonTokens.inAmount[carbonTokens.tokenIds[j]]
                            .mul(1000000));
                        offsets = offsets.add(carbonTokens.outAmount[carbonTokens.tokenIds[j]]);
                    }
                    total = total.add(carbonTokens.inAmount[carbonTokens.tokenIds[j]]
                        .mul(1000000).mul(ci).div(1000000));              
                }
            }
            if(i<trackerMappings.trackerIds.length){
                trackerId = trackerMappings.trackerIds[i];
                //usedTrackerIds.push(trackerId);
                usedTrackerIds[usedTrackerIds.length-1] = usedTrackerIds[usedTrackerIds.length-1].add(1);
                usedTrackerIds[usedTrackerIds[usedTrackerIds.length-1]]=trackerId;
                carbonTokens = trackerMappings.carbonTokens[trackerId];
            }
        }
        if(tokenTypeId==2 && offsets>0){
            return(total.div(offsets));
        }
        if(denominator==0){
            if(tokenTypeId==3 && trackerData.totalAudited>0){
                return total.div(trackerData.totalAudited);
            }
            // if deonminator ==0 there can be no tracked typeID==4 
        }else{
            if(tokenTypeId==3){
                return total.div(trackerData.totalEmissions);
            }else if(tokenTypeId==4 && trackerData.totalEmissions>0){
                total = total.add(outEmbedded).sub(
                    trackerData.totalAudited.mul(total.div(trackerData.totalEmissions))
                );
            }
        // subtract out the total embodied emission tied to outgoing 
        // audited emission certificates
        // TO-DO: GAMING OPPORTUNITY
        // auditor could be paid to issue fake emission certificats on tracker
        // to reduce the reportec carbon intensity rating of outgoing carbon tracker tokens
            return(total.div(denominator));
        } 
        return(0);  
    }
    function contains(uint[MAX_NESTED_TRACKERS] memory usedTrackerIds, uint trackerId) internal pure returns(bool){
        for (uint i = 0; i < usedTrackerIds.length-1; i++) {
            // the last array element is the last index stored
            if(usedTrackerIds[i]==trackerId){
                return true;
            }
        }
        return false;
    }
    /**
     * @dev returns number of unique trackers
     */
    function getNumOfUniqueTrackers() public view returns (uint256) {
        return _numOfUniqueTrackers.current();
    }
    /**
     * @dev returns the details of a given tokenId
     * @param trackerId token to check
     */
    function getTrackerDetails(uint256 trackerId)
        external
        view
        returns (
            CarbonTrackerDetails memory)
    {
        return (_trackerData[trackerId]);
    }
    function getTrackerIds(uint trackerId) public view returns(uint[] memory) {
        return (_trackerMappings[trackerId].trackerIds);
    }
    function getTokenIds(uint trackerId, uint sourceTrackerId) 
        public view returns(uint[] memory, uint) {
        return ( _trackerMappings[trackerId].carbonTokens[sourceTrackerId].tokenIds,
                 _trackerMappings[trackerId].trackerIndex[sourceTrackerId]);
    }
    function getTokenIdOut(uint trackerId, uint tokenId) 
        public view returns(uint, uint) {
        return ( _trackerMappings[trackerId].totalOut[tokenId],
                 _trackerMappings[trackerId].totalTracked[tokenId]);
    }
    function getTokenIdAmounts(uint trackerId, uint sourceTrackerId, uint tokenId) 
        public view returns(uint, uint){
            CarbonTokens storage carbonTokens = 
                _trackerMappings[trackerId].carbonTokens[sourceTrackerId];
            return( carbonTokens.inAmount[tokenId],
                    carbonTokens.outAmount[tokenId]);
        }
    function getTokenAmounts(uint trackerId, uint sourceTrackerId) 
        public view returns(uint[] memory,uint[] memory, uint[] memory) {
        CarbonTrackerMappings storage trackerMappings = _trackerMappings[trackerId];
        CarbonTokens storage carbonTokens;
        carbonTokens = trackerMappings.carbonTokens[sourceTrackerId];
        uint[] memory tokenIds = carbonTokens.tokenIds;
        uint[] memory inAmounts = new uint[](tokenIds.length);
        uint[] memory outAmounts = new uint[](tokenIds.length);
        for (uint j = 0; j < tokenIds.length; j++) {
            inAmounts[j]=carbonTokens.inAmount[tokenIds[j]];
            outAmounts[j]=carbonTokens.outAmount[tokenIds[j]];
        }
        return (tokenIds,inAmounts,outAmounts);
    }
}","keccak256":"0x818951f645a359288e20db0a8570278118409b67b426680b30bd08773646da82","license":"Apache-2.0"},"contracts/NetEmissionsTokenNetwork.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
contract NetEmissionsTokenNetwork is Initializable, ERC1155Upgradeable, AccessControlUpgradeable {

    using SafeMathUpgradeable for uint256;
    using CountersUpgradeable for CountersUpgradeable.Counter;
    using ECDSAUpgradeable for bytes32;
    using ECDSAUpgradeable for address;

    bool public limitedMode;  // disables some features like arbitrary token transfers and issuing without proposals
    address public admin;     // address that has permission to register dealers, transfer in limitedMode, etc.
    address private timelock; // DAO contract that executes proposals to issue tokens after a successful vote

    // Generic dealer role for registering/unregistering consumers
    bytes32 public constant REGISTERED_DEALER =
        keccak256("REGISTERED_DEALER");
    // Token type specific roles
    bytes32 public constant REGISTERED_REC_DEALER =
        keccak256("REGISTERED_REC_DEALER");
    bytes32 public constant REGISTERED_OFFSET_DEALER =
        keccak256("REGISTERED_OFFSET_DEALER");
    bytes32 public constant REGISTERED_EMISSIONS_AUDITOR =
        keccak256("REGISTERED_EMISSIONS_AUDITOR");
    // Consumer role
    bytes32 public constant REGISTERED_CONSUMER =
        keccak256("REGISTERED_CONSUMER");
    // Industry role (voluntary/nominate)
    bytes32 public constant REGISTERED_INDUSTRY =
        keccak256("REGISTERED_INDUSTRY");
    // Registered Industry Dealer (admin assignment)
    bytes32 public constant REGISTERED_INDUSTRY_DEALER =
        keccak256("REGISTERED_INDUSTRY_DEALER");

    /**
     * @dev Structure of all tokens issued in this contract
     * tokenId - Auto-increments whenever new tokens are issued
     * tokenTypeId - Corresponds to the three token types:
     *   1 => Renewable Energy Certificate
     *   2 => Carbon Emissions Offset
     *   3 => Audited Emissions
     *   4 => Carbon Tracker tokens (traded, burnt or stored fuel/feed stock)
     *   TO-DO define carbon tracker storage transactions (i.e. captured CO2 management) 
     * issuer - Address of dealer issuing this token
     * issuee - Address of original issued recipient this token
     * fromDate - Unix timestamp
     * thruDate - Unix timestamp
     * dateCreated - Unix timestamp
     * automaticRetireDate - Unix timestamp
     */
    struct CarbonTokenDetails {
        uint256 tokenId;
        uint8 tokenTypeId;
        address issuer;
        address issuee;
        uint256 fromDate;
        uint256 thruDate;
        uint256 dateCreated;
        uint256 automaticRetireDate;
        string metadata;
        string manifest;
        string description;
        uint256 totalIssued;
        uint256 totalRetired;
    }


    // Counts number of unique token IDs (auto-incrementing)
    CountersUpgradeable.Counter private _numOfUniqueTokens;

    // Token metadata and retired balances
    mapping(uint256 => CarbonTokenDetails) private _tokenDetails;
    mapping(uint256 => mapping(address => uint256)) private _retiredBalances;
    mapping(uint256 => mapping(address => uint256)) private _transferredBalances;

    // Nonce for tokeTypeId 4 transfer from => to account
    mapping(address => mapping(address => uint32)) private carbonTransferNonce;

    // Events
    event TokenCreated(
        uint256 availableBalance,
        uint256 retiredBalance,
        uint256 tokenId,
        uint8 tokenTypeId,
        address indexed issuer,
        address indexed issuee,
        uint256 fromDate,
        uint256 thruDate,
        uint256 dateCreated,
        uint256 automaticRetireDate,
        string metadata,
        string manifest,
        string description
    );
    event TokenRetired(
        address indexed account,
        uint256 tokenId,
        uint256 amount
    );
    event RegisteredConsumer(address indexed account);
    event UnregisteredConsumer(address indexed account);
    event RegisteredDealer(address indexed account);
    event UnregisteredDealer(address indexed account);
    event RegisteredIndustry(address indexed account);
    event UnregisteredIndustry(address indexed account);

    // Replaces constructor in OpenZeppelin Upgrades
    function initialize(address _admin) public initializer {

        __ERC1155_init("");

        // Allow dealers to register consumers
        _setRoleAdmin(REGISTERED_CONSUMER, REGISTERED_DEALER);

        // Set-up admin
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
        _setupRole(REGISTERED_DEALER, _admin);
        _setupRole(REGISTERED_REC_DEALER, _admin);
        _setupRole(REGISTERED_OFFSET_DEALER, _admin);
        _setupRole(REGISTERED_EMISSIONS_AUDITOR, _admin);
        _setupRole(REGISTERED_INDUSTRY_DEALER, _admin);
        _setupRole(REGISTERED_INDUSTRY, _admin);
        admin = _admin;

        // initialize
        timelock = address(0);

        limitedMode = false;
    }

    modifier onlyAdmin() {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "CLM8::onlyAdmin: msg.sender not the admin"
        );
        _;
    }

    modifier consumerOrDealer(address from,address to) {
        if(from!=address(0)){
            // if not minting require sender to be consumerOrDealer 
            require(_consumerOrDealer(from),
                "CLM8::consumerOrDealer: sender not a consumer or a dealer");
        }
        if(to!=address(0)){
            // if not burning require receiver is consumerOrDealer 
            require(_consumerOrDealer(to),
                "CLM8::consumerOrDealer: recipient must be consumer, dealer or industry"); 
        }
        _;
    }

    modifier onlyDealer() {
        _onlyDealer();
        _;
    }

    /**
     * @dev Returns `true` if hasRole of dealer
     */
    function _onlyDealer() internal view {
        /*
        bool isRecDealer = hasRole(REGISTERED_REC_DEALER, msg.sender);
        bool isCeoDealer = hasRole(REGISTERED_OFFSET_DEALER, msg.sender);
        bool isAeDealer = hasRole(REGISTERED_EMISSIONS_AUDITOR, msg.sender);
        bool isInDealer = hasRole(REGISTERED_INDUSTRY_DEALER, msg.sender);
        */
        require(
            //hasRole(REGISTERED_REC_DEALER, msg.sender) ||
            //hasRole(REGISTERED_OFFSET_DEALER, msg.sender) ||
            //hasRole(REGISTERED_EMISSIONS_AUDITOR, msg.sender) ||
            //hasRole(REGISTERED_INDUSTRY_DEALER, msg.sender) ||
            // the below will achieve the same as the above
            hasRole(REGISTERED_DEALER,msg.sender) || 
            // REGISTERED_INDSUTRY are considered dealers of carbon tokens
            // but have not be assigned REGISTERED_DEALER role by admin
            hasRole(REGISTERED_INDUSTRY,msg.sender),
            "CLM8::onlyDealer: msg.sender not a dealer"
        );
    }

    /**
     * @dev Returns `true` if hasRole of dealer, industry or consumer
     */
    function _consumerOrDealer(address entity) public view returns (bool) {
        // check for one role and return if true if true
        // before checking the next to minimize gas
        if(hasRole(REGISTERED_DEALER, entity) ||
           hasRole(REGISTERED_CONSUMER, entity) ||
           hasRole(REGISTERED_INDUSTRY, entity) 
        ) return true;
        return false;
    }

    /**
     * @dev Returns `true` if transfer has been approved by to address
     * reconstruct transferHash and check that it matches the signature
     */
    function verifySignature(
        bytes32 msgHash,
        bytes memory signature,
        address signer
    ) public pure returns (bool){   
        bytes32 ethSignedMessageHash = msgHash.toEthSignedMessageHash();
        return ethSignedMessageHash.recover(signature)==signer;
    }
    /**
     * @dev Returns keccak256 hash of transaction request
     * including next available nonce for transfer from -> to addresses
     */
    function getTransferHash(
        address _from, 
        address _to, 
        uint256[] memory _ids, 
        uint256[] memory _amounts
    ) public view returns (bytes32) {
        return keccak256(abi.encodePacked(_from, _to, _ids, _amounts, carbonTransferNonce[_from][_to]+1));
    }

    /**
     * @dev returns true if the tokenId exists
     */
    function tokenExists(uint256 tokenId) private view returns (bool) {
        if (_numOfUniqueTokens.current() >= tokenId) return true;
        return false; // no matching tokenId
    }

    /**
     * @dev returns true if the tokenTypeId is valid
     */
    function tokenTypeIdIsValid(uint8 tokenTypeId) pure private returns (bool) {
        if ((tokenTypeId > 0) && (tokenTypeId <= 4)) {
            return true;
        }
        return false; // no matching tokenId
    }

    /**
     * @dev returns number of unique tokens
     */
    function getNumOfUniqueTokens() public view returns (uint256) {
        return _numOfUniqueTokens.current();
    }

    function supportsInterface(bytes4 interfaceId) public view virtual 
        override(ERC1155Upgradeable,AccessControlUpgradeable) returns (bool) {
        return interfaceId == type(IAccessControlUpgradeable).interfaceId || super.supportsInterface(interfaceId);
    }

    /**
     * @dev hook to prevent transfers from non-admin account if limitedMode is on
     * @param data signature of getTransferHash() for transfer of carbon token type (id=4)
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        internal
        virtual
        override
        consumerOrDealer(from,to)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        // TO-DO this could be set as a modifier ...
        require((from != to), "CLM8::_beforeTokenTransfer: sender and receiver cannot be the same");
        bool approveCarbon; // bool if we need to approve the transfer of carbon tokens
        for (uint i = 0; i < ids.length; i++) {

            CarbonTokenDetails storage token = _tokenDetails[ids[i]];
            // disable most transfers if limitedMode is on
            if (limitedMode) {

                // allow retiring/burning one's tokens
                if (to == address(0)) {
                    continue;
                }

                // for tokenType 1 and 2, only the timelock and DAO can transfer/issue
                // for tokenType 3, only emissions auditors can transfer/issue
                // (and they are automatically retired right after)
                if (token.tokenTypeId == 1 || token.tokenTypeId == 2 ) {
                    require(
                        operator == timelock || hasRole(DEFAULT_ADMIN_ROLE, operator),
                        "CLM8::_beforeTokenTransfer(limited): only admin and DAO can transfer tokens"
                    );
                } else if (token.tokenTypeId == 3) {
                    require(
                        hasRole(REGISTERED_EMISSIONS_AUDITOR, operator),
                        "CLM8::_beforeTokenTransfer(limited): only emissions auditors can issue audited emissions"
                    );
                }
            }
            // for tokenType 4, any authorized operator can
            // issue (from == address(0)) or
            // burn/retire (to == address(0))
            // otherwise require receiver (to address) to have approved (signed) the transferHash
            if(token.tokenTypeId == 4 && to != address(0) && from != address(0)) {
                approveCarbon = false;//true;
                // TO-DO: drop internal approval of carbon transfers?
                // voluntary carbon tracker token can be sent to anyone to use in the C-NFT
                // they can be sent without approval inviting the receiver to track them to their NFT
                // accumulate total transferred balances (not minted or burnt)
                _transferredBalances[token.tokenId][from] =
                    _transferredBalances[token.tokenId][from].add(amounts[i]);
            }

        }
        if(approveCarbon){
            bytes32 messageHash = getTransferHash(from,to,ids,amounts);
            require(
                verifySignature(messageHash, data, to),
                "CLM8::_beforeTokenTransfer: receiver's approval signature is not valid"
            );
            //increment the nonce once transaction has been confirmed
            carbonTransferNonce[from][to]++;
        }

    }

    /**
     * @dev External function to mint an amount of a token
     * Only authorized dealer of associated token type can call this function
     * @param quantity of the token to mint For ex: if one needs 100 full tokens, the caller
     * should set the amount as (100 * 10^4) = 1,000,000 (assuming the token's decimals is set to 4)
     */
    function issue(
        address issuee,
        uint8 tokenTypeId,
        uint256 quantity,
        uint256 fromDate,
        uint256 thruDate,
        uint256 automaticRetireDate,
        string memory metadata,
        string memory manifest,
        string memory description
    ) public onlyDealer {
        return _issue(
            issuee,
            msg.sender,
            tokenTypeId,
            quantity,
            fromDate,
            thruDate,
            automaticRetireDate,
            metadata,
            manifest,
            description
        );
    }

    /**
     * @dev Issue function for DAO (on limited mode) or admin to manually pass issuer
     * Must be called from Timelock contract through a successful proposal
     * or by admin if limited mode is set to false
     */
    function issueOnBehalf(
        address issuee,
        address issuer,
        uint8 tokenTypeId,
        uint256 quantity,
        uint256 fromDate,
        uint256 thruDate,
        uint256 automaticRetireDate,
        string memory metadata,
        string memory manifest,
        string memory description
    ) public {

        require(
            (msg.sender == timelock) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "CLM8::issueOnBehalf: call must come from DAO or admin"
        );

        return _issue(
            issuee,
            issuer,
            tokenTypeId,
            quantity,
            fromDate,
            thruDate,
            automaticRetireDate,
            metadata,
            manifest,
            description
        );
    }

    function _issue(
        address _issuee,
        address _issuer,
        uint8 _tokenTypeId,
        uint256 _quantity,
        uint256 _fromDate,
        uint256 _thruDate,
        uint256 _automaticRetireDate,
        string memory _metadata,
        string memory _manifest,
        string memory _description
    ) internal {

        require(
            tokenTypeIdIsValid(_tokenTypeId),
            "CLM8::_issue: tokenTypeId is invalid"
        );

        if (limitedMode) {
            if (_tokenTypeId == 1 || _tokenTypeId == 2 ) {
                require(
                    msg.sender == timelock,
                    "CLM8::_issue(limited): msg.sender not timelock"
                );
                require(
                    hasRole(DEFAULT_ADMIN_ROLE, _issuee),
                    "CLM8::_issue(limited): issuee not admin"
                );
                require(
                    hasRole(REGISTERED_REC_DEALER, _issuer) || hasRole(REGISTERED_OFFSET_DEALER, _issuer),
                    "CLM8::_issue(limited): proposer not a registered dealer"
                );
            } else if (_tokenTypeId == 3) {
                require(
                    hasRole(REGISTERED_EMISSIONS_AUDITOR, _issuer),
                    "CLM8::_issue(limited): issuer not a registered emissions auditor"
                );
            }
        } else {
            if (_tokenTypeId == 1) {
                require(
                    hasRole(REGISTERED_REC_DEALER, _issuer),
                    "CLM8::_issue: issuer not a registered REC dealer"
                );
            } else if (_tokenTypeId == 2) {
                require(
                    hasRole(REGISTERED_OFFSET_DEALER, _issuer),
                    "CLM8::_issue: issuer not a registered offset dealer"
                );
            } else if (_tokenTypeId == 3) {
                require(
                    hasRole(REGISTERED_EMISSIONS_AUDITOR, _issuer),
                    "CLM8::_issue: issuer not a registered emissions auditor"
                );
            }
        }
        // TO-DO: Define limited mode for tokenTypeId 4?
        if (_tokenTypeId == 4) {
            require(
                hasRole(REGISTERED_INDUSTRY, _issuer),
                "CLM8::_issue: issuer not a registered industry"
            );
            require(
                msg.sender == _issuee,
                "CLM8::_issue: registered industry can only issue carbon to itself"
            );
        } 

        // increment token identifier
        _numOfUniqueTokens.increment();

        // create token details
        CarbonTokenDetails storage tokenInfo = _tokenDetails[_numOfUniqueTokens.current()];

        tokenInfo.tokenId = _numOfUniqueTokens.current();
        tokenInfo.tokenTypeId = _tokenTypeId;
        tokenInfo.issuee = _issuee;
        tokenInfo.issuer = _issuer;
        tokenInfo.fromDate = _fromDate;
        tokenInfo.thruDate = _thruDate;
        tokenInfo.automaticRetireDate = _automaticRetireDate;
        tokenInfo.dateCreated = block.timestamp;
        tokenInfo.metadata = _metadata;
        tokenInfo.manifest = _manifest;
        tokenInfo.description = _description;
        tokenInfo.totalIssued = _quantity;
        tokenInfo.totalRetired = uint256(0);

        super._mint(_issuee, _numOfUniqueTokens.current(), _quantity, "");

        // retire audited emissions on mint
        if (_tokenTypeId == 3) {
            _retire(tokenInfo.issuee, tokenInfo.tokenId, _quantity);
        }

        // emit event with all token details and balances
        emit TokenCreated(
            _quantity,
            _retiredBalances[tokenInfo.tokenId][tokenInfo.issuee],
            tokenInfo.tokenId,
            tokenInfo.tokenTypeId,
            tokenInfo.issuer,
            tokenInfo.issuee,
            tokenInfo.fromDate,
            tokenInfo.thruDate,
            tokenInfo.dateCreated,
            tokenInfo.automaticRetireDate,
            tokenInfo.metadata,
            tokenInfo.manifest,
            tokenInfo.description
        );
    }

    /**
     * @dev mints more of an existing token
     * @param to reciepient of token
     * @param tokenId token to mint more of
     * @param quantity amount to mint
     */
     // To DO - this will increase _balances of to account
     // but will not update the totalIssued ?
    function mint(address to, uint256 tokenId, uint256 quantity)
        external
        onlyAdmin
    {
        require(tokenExists(tokenId), "CLM8::mint: tokenId does not exist");
        require(!limitedMode, "CLM8::mint: cannot mint new tokens in limited mode");
        super._mint(to, tokenId, quantity, "");
    }

    /**
     * @dev returns the token name for the given token as a string value
     * @param tokenId token to check
     */
    function getTokenType(uint256 tokenId)
        external
        view
        returns (string memory)
    {
        require(tokenExists(tokenId), "CLM8::getTokenType: tokenId does not exist");
        CarbonTokenDetails storage token = _tokenDetails[tokenId];

        if (token.tokenTypeId == 1) {
            return "Renewable Energy Certificate";
        } else if (token.tokenTypeId == 2) {
            return "Carbon Emissions Offset";
        } else if (token.tokenTypeId == 3) {
            return "Audited Emissions";
        } else if (token.tokenTypeId == 4) {
            return "Carbon Tracker";
        } else {
            return "Token does not exist";
        }
    }
    function getTokenTypeId(uint tokenId) external view returns(uint8){
        return _tokenDetails[tokenId].tokenTypeId;
    }
    function getRetiredBalances(uint tokenId, address account) 
        external view returns(uint){
        return _retiredBalances[tokenId][account];
    }
    function getTransferredBalances(uint tokenId, address account) 
        external view returns(uint){
        return _transferredBalances[tokenId][account];
    }

    /**
     * @dev returns the retired amount on a token
     * @param tokenId token to check
     */
    function getTokenRetiredAmount(address account, uint256 tokenId)
        public
        view
        returns (uint256)
    {
        require(tokenExists(tokenId), "CLM8::getTokenRetiredAmount: tokenId does not exist");
        uint256 amount = _retiredBalances[tokenId][account];
        return amount;
    }

    /**
     * @dev sets the token to the retire state to disable transfers, mints and burns
     * @param tokenId token to set in pause state
     *   Only contract owner can pause or resume tokens
     */
    function retire(
        uint256 tokenId,
        uint256 amount
    ) external consumerOrDealer(msg.sender,address(0)) {
        require(tokenExists(tokenId), "CLM8::retire: tokenId does not exist");
        // TO-DO do we need this require statement? super._burn sub() will require this ...
        require( (amount <= super.balanceOf(msg.sender, tokenId)), "CLM8::retire: not enough available balance to retire" );

        _retire(msg.sender, tokenId, amount);
        emit TokenRetired(
            msg.sender,
            tokenId,
            amount
        );
    }

    function _retire(
        address _address,
        uint256 tokenId,
        uint256 _quantity
    ) internal {
        super._burn(_address, tokenId, _quantity);
        _tokenDetails[tokenId].totalRetired = _tokenDetails[tokenId].totalRetired.add(_quantity);
        _retiredBalances[tokenId][_address] = _retiredBalances[tokenId][_address].add(_quantity);
    }


    /**
     * @dev returns true if Dealer's account is registered
     * @param account address of the dealer
     */
    function isDealerRegistered(address account) public view returns (bool) {
        if (hasRole(REGISTERED_REC_DEALER, account) ||
            hasRole(REGISTERED_OFFSET_DEALER, account) ||
            hasRole(REGISTERED_EMISSIONS_AUDITOR, account) ||
            hasRole(REGISTERED_INDUSTRY_DEALER, account) 
        ) return true;
        return false;
    }

    /**
     * @dev returns true if Consumers's account is registered
     * @param account address of the dealer
     */
    function isConsumerRegistered(address account) public view returns (bool) {
        return hasRole(REGISTERED_CONSUMER, account);
    }

    /**
     * @dev returns true if Consumers's or Dealer's account is registered
     * @param account address of the consumer/dealer
     */
    function isRegisteredDealerOrConsumer(address account) private view returns (bool) {
        return (isDealerRegistered(account) || isConsumerRegistered(account));
    }

    /**
     * @dev Helper function for returning tuple of bools of role membership
     * @param account address to check roles
     */
    function getRoles(address account) external view returns (bool, bool, bool, bool, bool, bool) {
        bool isAdmin = hasRole(DEFAULT_ADMIN_ROLE, account);
        bool isRecDealer = hasRole(REGISTERED_REC_DEALER, account);
        bool isCeoDealer = hasRole(REGISTERED_OFFSET_DEALER, account);
        bool isAeDealer = hasRole(REGISTERED_EMISSIONS_AUDITOR, account);
        bool isIndustry = hasRole(REGISTERED_INDUSTRY, account);
        bool isConsumer = hasRole(REGISTERED_CONSUMER, account);
        return (isAdmin, isRecDealer, isCeoDealer, isAeDealer, isIndustry, isConsumer);
    }

    /**
     * @dev Only contract owner can register Dealers
     * @param account address of the dealer to register
     */
    function registerDealer(address account, uint8 tokenTypeId)
        public
        onlyAdmin
    {
        require(tokenTypeIdIsValid(tokenTypeId), "CLM8::registerDealer: tokenTypeId does not exist");
        if (tokenTypeId == 1) {
            grantRole(REGISTERED_REC_DEALER, account);
        } else if (tokenTypeId == 2) {
            grantRole(REGISTERED_OFFSET_DEALER, account);
        } else if (tokenTypeId == 3){
            grantRole(REGISTERED_EMISSIONS_AUDITOR, account);
        } else if (tokenTypeId == 4) {
            grantRole(REGISTERED_INDUSTRY, account);
            grantRole(REGISTERED_INDUSTRY_DEALER, account);
        }
        // Also grant generic dealer role for registering/unregistering dealer
        grantRole(REGISTERED_DEALER, account);
        emit RegisteredDealer(account);
    }

    /**
     * @dev msg.sender can volunteer themselves as registered industry
     * or other registered dealer can register Industry
     */
    function registerIndustry(address account) external
    {
        if(msg.sender != account){
            // only dealer can register industry
            _onlyDealer();
        }
        if (limitedMode) {
            require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "CLM8::registerIndustry(limited): only admin can register industries");
        }
        _setupRole(REGISTERED_INDUSTRY, account);
        emit RegisteredIndustry(account);
    }

    /**
     * @dev returns true if Consumer's account is registered for the given token
     * @param account address of the consumer
     */
    function registerConsumer(address account) external onlyDealer {
        if (limitedMode) {
            require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "CLM8::registerConsumer(limited): only admin can register consumers");
        }
        grantRole(REGISTERED_CONSUMER, account);
        emit RegisteredConsumer(account);
    }

    /**
     * @dev Only contract owner can unregister Dealers
     * @param account address to be unregistered
     */
    function unregisterDealer(address account, uint8 tokenTypeId)
        external
        onlyAdmin
    {
        require(tokenTypeIdIsValid(tokenTypeId), "CLM8::unregisterDealer: tokenTypeId does not exist");
        if (tokenTypeId == 1) {
            super.revokeRole(REGISTERED_REC_DEALER, account);
        } else if (tokenTypeId == 2) {
            super.revokeRole(REGISTERED_OFFSET_DEALER, account);
        } else if (tokenTypeId == 3) {
            super.revokeRole(REGISTERED_EMISSIONS_AUDITOR, account);
        } else if (tokenTypeId == 4) {
            super.revokeRole(REGISTERED_INDUSTRY_DEALER, account);
        }
        // If no longer a dealer of any token type, remove generic dealer role
        if (!isDealerRegistered(account)) {
            revokeRole(REGISTERED_DEALER, account);
        }

        emit UnregisteredDealer(account);
    }

    /**
     * @dev Only contract owner can unregister Consumers
     * TO-DO clarify the above - function allows any Dealer to unregister consumers.
     * Why would we need to unregister consumers?
     * @param account address to be unregistered
     */
    function unregisterConsumer(address account) external onlyDealer {
        if (limitedMode) {
            require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "CLM8::unregisterConsumer(limited): only admin can unregister consumers");
        }
        super.revokeRole(REGISTERED_CONSUMER, account);
        emit UnregisteredConsumer(account);
    }

    /**
     * @dev transfers the value to the 'to' account for tokenId
     * @param to recipient address
     * @param tokenId tokenId for the transfer
     * @param value amount of transfer
     * Transfer can start only when both parties are registered and the token is not paused
     * Note: Token holders can arbitrarily call safeTransferFrom() without these checks
     * The requires commented out below have been moved to _beforeTokenTransfer hook
     * so that they are always applied to safeTransferFrom (or safeBatch...)      
     */
    function transfer(
        address to,
        uint256 tokenId,
        uint256 value
    ) external {
        require(tokenExists(tokenId), "CLM8::transfer: tokenId does not exist");

        /*
        require((msg.sender != to), "CLM8::transfer: sender and receiver cannot be the same");
        receiver must be Consumer or Dealer or Industry
        require(
            hasRole(REGISTERED_CONSUMER, to) || 
            hasRole(REGISTERED_DEALER, to) || 
            hasRole(REGISTERED_INDUSTRY, to),
            "CLM8::transfer: Recipient must be consumer, industry, or dealer"
        ); */
        super.safeTransferFrom(msg.sender, to, tokenId, value, '0x00');
    }

    function setTimelock(
        address _timelock
    ) external onlyAdmin {
        timelock = _timelock;
    }

    function getAvailableAndRetired(address account, uint256 tokenId)
        external
        view
        returns (uint256, uint256)
    {
        uint256 available = super.balanceOf(account, tokenId);
        uint256 retired = this.getTokenRetiredAmount(account, tokenId);
        return (available, retired);
    }
    function getAvailableRetiredAndTransferred(address account, uint256 tokenId)
        external
        view
        returns (uint256, uint256, uint256)
    {
        uint256 available;
        uint256 retired;
        (available,retired) = this.getAvailableAndRetired(account, tokenId);
        uint256 transferred = _transferredBalances[tokenId][account];
        return (available, retired, transferred);
    }

    /**
     * @dev returns issuer of a given tokenId
     */
    function getIssuer(uint256 tokenId)
        external
        view
        returns (address)
    {
        return _tokenDetails[tokenId].issuer;
    }

    /**
     * @dev returns the details of a given tokenId, omitting holders
     * @param tokenId token to check
     */
    function getTokenDetails(uint256 tokenId)
        external
        view
        returns (CarbonTokenDetails memory)
    {
        return _tokenDetails[tokenId];
    }

    /**
     * @dev turns off or on limited mode
     * @param _limitedMode boolean value
     */
    function setLimitedMode(bool _limitedMode)
        external
        onlyAdmin
    {
        limitedMode = _limitedMode;
    }

    function isAuditor(address auditor) view external returns (bool) {
        return hasRole(REGISTERED_EMISSIONS_AUDITOR, auditor);
    }

}
